        getCollatzConjecture(num);
      }

      setGraphicalData();
    };

    // End coding here
  }, [number]);

  return (
    <div className="App">
      <input
        type={"number"}
        onChange={(e) => {
          setNumber(e.target.value);
        }}
      />
      <div>
        <HighchartsReact highcharts={Highcharts} options={options} />
      </div>
    </div>
  );
};

export default App;
