import logo from "./logo.svg";
import React, { useEffect, useState } from "react";
import "./App.css";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

const App = () => {
  const [number, setNumber] = useState(0);
  const [sequence, setSequence] = useState([]);
  const options = {
    chart: {
      type: "spline",
    },
    title: {
      text: "My chart",
    },
    series: [
      {
        data: sequence,
      },
    ],
  };

  useEffect(() => {
    // Start coding here
    let collatzConjectureData = [];

    function getCollatzConjecture(num) {
      collatzConjectureData = collatzConjectureData.concat(num);

      if (num === 0) return 0;
      else if (num === 1) return 1;
      else if (num % 2 === 0) return getCollatzConjecture(num / 2);
      else return getCollatzConjecture(num * 3 + 1);
    }

    return () => {
      console.log(number, numberr);
      num = numberr;

      //check if input is a positive integer
      let x = parseFloat(num);
      if (!isNaN(num) && (x | 0) === x && num >= 0) {
        num = parseInt(num);
        collatzConjectureData = [];
        getCollatzConjecture(num);
      }

      setSequence(collatzConjectureData);
    };

    // End coding here
  }, [number]);

  let numberr = 0;
  function readInput(event) {
    let num = event.target.value;
    console.log(num);
    setNumber(num);
  }

  return (
    <div className="App">
      <input
        type={"number"}
        onChange={(e) => {
          numberr = e.target.value;
          setNumber(num);
        }}
      />
      {/* <input type={"number"} onChange={readInput} /> */}
      <div>
        <HighchartsReact highcharts={Highcharts} options={options} />
      </div>
    </div>
  );
};

export default App;
